module.exports = {
  exactMatch(jsonObj, regex){
    let REGEX = new RegExp( '^' + regex + '$'),
        words = Object.keys( jsonObj );

    return words.filter((word) => {
      return word.match(REGEX);
    });
  },

  partialMatch(jsonObj, regex){
    let REGEX = new RegExp( regex ),
        words = Object.keys( jsonObj );

    return words.filter((word) => {
      return word.match(REGEX);
    });
  },

  beginsWithMatch(jsonObj, regex){
    let REGEX = new RegExp( '^' + regex, 'i' ),
        words = Object.keys( jsonObj );

    return words.filter((word) => {
      return word.match(REGEX);
    });
  },

  endsWithMatch(jsonObj, regex){
    let REGEX = new RegExp( regex + '$', 'i'),
    words = Object.keys( jsonObj );

    return words.filter((word) => {
      return word.match(REGEX);
    });
  }
//  
};